// @flow

/**
 * The type of redux action dispatched which represents that the blur
 * is enabled.
 *
 * {
 *      type: BLUR_ENABLED
 * }
 */
export const BLUR_ENABLED = 'BLUR_ENABLED';

/**
 * The type of redux action dispatched which represents that the blur
 * is disabled.
 *
 * {
 *      type: BLUR_DISABLED
 * }
 */
export const BLUR_DISABLED = 'BLUR_DISABLED';
